 xinput create-master laptops ; laptopskeyboard="$(xinput list --id-only 'laptops keyboard')" ;laptopspointer="$(xinput list --id-only 'laptops pointer')" ; xinput reattach $(xinput list --id-only "AT Translated Set 2 keyboard") $laptopskeyboard ; xinput reattach $(xinput list --id-only "SYNA32A5:00 06CB:CE17 Mouse") $laptopspointer ; xinput reattach $(xinput list --id-only "SYNA32A5:00 06CB:CE17 Touchpad") $laptopspointer ; windowid=$(xwininfo|grep -o -E "Window id: .{9}"|grep -o -E ".x.{7}") ; for i in $laptopspointer $laptopskeyboard;do xinput set-cp $windowid $i;done ; windowid=$(xwininfo|grep -o -E "Window id: .{9}"|grep -o -E ".x.{7}") ; for i in $(xinput list --id-only "Virtual core pointer") $(xinput list --id-only "Virtual core keyboard");do xinput set-cp $windowid $i;done